package javapro;

public class Wrapper {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		Integer a=10;
		int i1=Integer.valueOf(a);
		System.out.println(i1);
		
	}

}
/* Wrapper class-> To create a primitive datatype to a Object
int-> Integer
float- Float
boolean- > Boolean
double--> Double

*/